		public DMC(int NRed,int NGreen, int NBlue)
		{
		 this.Number = 99  ;
		 this.Name = "Perso";
		 this.Couleur = (7*NRed)+(3*NBlue)+(5*NGreen)+(NRed*NGreen*NBlue);
		}

		public DMC(int NNumber)
		{
		 this.Number = NNumber;
		 this.Name = (string)Names.GetValue(this.Number);
		 this.Couleur = (double)Couleurs.GetValue(this.Number);
		}

		public DMC(double NCouleur)
